        static string GetSite(string provinceCode, string cityCode, ref byte province, ref short city)
        {
            if (!byte.TryParse(provinceCode, out province))
                return "";
            if (!mProvinceCode.ContainsKey(province))
                return "";
            provinceCode = mProvinceCode[province];
            if (!short.TryParse(cityCode, out city))
                return "";
            List<short> list = new List<short>(mCityCode[province]);
            if (!list.Contains(city))
                return "";
            string[] listStr = mCityName[province];
            int index = list.IndexOf(city);
            if (index < 0 || index >= listStr.Length)
                return "";
            cityCode = listStr[index];

            return provinceCode + cityCode;
        }
        static bool GetBirthy(string birthyCode, ref DateTime d)
        {
            if (birthyCode.Length != 8)
                return false;
            string yearCode = birthyCode.Substring(0, 4);
            int year = 0;
            if (!int.TryParse(yearCode, out year))
                return false;
            int nowYear = DateTime.Now.Year;
            if (year < nowYear - 120 || year > DateTime.Now.Year - 3)
                return false;
            string monthCode = birthyCode.Substring(4, 2);
            int month = 0;
            if (!int.TryParse(monthCode, out month))
                return false;
            if (month < 0 || month > 12)
                return false;
            string dayCode = birthyCode.Substring(6);
            int day = 0;
            if (!int.TryParse(dayCode, out day))
                return false;
            if (day < 0 || day > 31)
                return false;
            d = new DateTime(year, month, day);
            return true;
        }
        public static string CheckIDCode(string code, ref byte province, ref short city, ref DateTime d)
        {
            if (code.Length != mSignParam.Length + 1)
                return "";
            string site = GetSite(code.Substring(0, 2), code.Substring(2, 4), ref province, ref city);
            if (string.IsNullOrEmpty(site))
                return "";
            if (!GetBirthy(code.Substring(6, 8), ref d))
                return "";
            char signCode = code[code.Length - 1];
            int num = 0;
            for(int i = 0; i < mSignParam.Length; ++ i)
            {
                byte c = byte.Parse(code[i].ToString());
                num += c * mSignParam[i];
            }
            num %= mSignNumDivide;
            if (signCode != mSignResult[num])
                return "";
            return site;
        }
    }
